package com.charith.hackerrank.StringManipulation.SherlockAndTheValidString;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

class SolutionTest {

    @Test
    void isValid() {
        Assertions.assertEquals("YES", Solution.isValid("abc"));

        Assertions.assertEquals("YES", Solution.isValid("abcc"));

        Assertions.assertEquals("NO", Solution.isValid("abccc"));

        Assertions.assertEquals("NO", Solution.isValid("aabbcd"));

        Assertions.assertEquals("NO", Solution.isValid("aabbccddeefghi"));

        Assertions.assertEquals("YES", Solution.isValid("abcdefghhgfedecba"));

        Assertions.assertEquals("YES", Solution.isValid(""));

        Assertions.assertEquals("NO", Solution.isValid(""));
    }
}